import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { Row, Col, ListGroupItem } from 'reactstrap';

// Images used for types, SVGs loaded into objects
import retailerIcon from '../rescources/retail.png';


class SearchResult extends Component {
	renderTypes(lic,types){
		let results = [];
		types.forEach((type)=>{
			let typePretty = type.replace(/-/g,' ');
			let typeKey = 'lic-'+lic+'-type-'+type;
			results.push(<span key={typeKey} className={'type-icon type-'+type} title={typePretty}><object type={"image/svg+xml"} data={retailerIcon}>SVG No Bueno!</object></span>)
		})
		return results
	}
	render(){ 
		const result = this.props.data;
		if(result===undefined || result.license===undefined){
			return null
		}else{
			// Lets look at the results and add some classes in
			let classes = ['search-result']
			let licStatus = '';

			if(result.active.substr(0,6)==='ACTIVE'){
				licStatus = ''; // No need to say active 911 times on the page
				classes.push('lic-active')

			}else if(result.active.substr(0,7)==='PENDING'){
				licStatus = 'Pending';
				classes.push('lic-pending')

			}else{
				licStatus = result.active;
				classes.push('lic-unknown')
			}

			// Get the types and add them as classes
			let types = result.type.replace(/MARIJUANA /g,'').toLowerCase().replace(/ /g,'-').split('/');
			classes.push(...types);

			// Prepare a pretty looking URL
			let urlPrettyOrg = result.organization.toLowerCase().replace(/[^a-z0-9]+/g,'-');

			// And finally the return
			return (
				<ListGroupItem key={'licnumkey'+result.license} className={classes.join(' ')}>
					<Link to={'/details/'+result.license+'/'+urlPrettyOrg}>
						<Row className={'d-flex'}>
							<Col xs="auto" className={'mr-auto'}>
								<h4>{result.organization}</h4>
							</Col>
							<Col xs="auto">
								<h5>License# {result.license}<b>{licStatus}</b></h5>
							</Col>
						</Row>
						<Row className={'d-flex'}>
							<Col xs="auto" className={'mr-auto'}>
								<address>{result.address+' '+result.city+', '+result.county+' county '+result.state}</address>
							</Col>
							<Col xs="auto">
								<div className={'type-icons'}>
									{this.renderTypes(result.license,types)}
								</div>
							</Col>
						</Row>
					</Link>
				</ListGroupItem>
			)			
		}
	}
}

export default SearchResult;

