import React, { Component } from 'react'
import { Container, Row, Col } from 'reactstrap';
// import { push } from 'connected-react-router'
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux'
// import ReactTable from "react-table";

class LicenseBox extends Component {
	constructor(props) {
		super(props);
		this.state = {
			displayed: 0,
		}
	}

	// This will check the nuber of licenses and display those buttons accordingly
	licenseNavDisplay = () => {
		if(this.state.licenseDetails.length>1){
			return (<span>
				<button onClick={this.updateDisplayedLicense} disabled={this.licenseNavDisabled(-1)} data-adjustment={-1} id={'lic-back-b'}>Back</button>
				<button onClick={this.updateDisplayedLicense} disabled={this.licenseNavDisabled( 1)} data-adjustment={1} id={'lic-forward-b'}>Forward</button>
			</span>)
		}else{
			return null
		}
	}

	render(){ 
		// If there are no lices this box should not show, can happen for a moment before the API gets back to us.
		if(this.props.licenses[this.state.displayed]===undefined){
			return null
		}else{
			let lic = this.props.licenses[this.state.displayed]; // easier to use lci than that whole thing
			return (<Container>
				<Row><Col tag={'h1'}>{lic.organization}</Col><Col>{this.licenseNavDisplay()}</Col></Row>
				<Row><Col><br/><pre>{JSON.stringify(this.props.licenses,null,2)}</pre></Col></Row>
			</Container>)

		}
	}
}

export default LicenseBox;
