import React, { Component } from 'react'
import { ListGroup, Container, Row, Col } from 'reactstrap';
import SearchResult from '../search-result'
// import { push } from 'connected-react-router'
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux'
// import ReactTable from "react-table";

// Components

class NaughtyList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			violators: {},
			violatorsOrdered: [],
			limitor: 100,
		}
		// Loop through all the licenses and grab the details we need
		this.getNaughty();
	}

	// This will grab the basic license information for the procided license number
	getLicenseDetails = (licNums) => {
		let licDetailsCall = ['https://data.lcb.wa.gov/resource/bhbp-x4eb.json?'];
		licDetailsCall.push('$where=license in ("'+licNums.join('","')+'") ')
		fetch(licDetailsCall.join('')).then(response => response.json()).then(this.storeLicenseDetails)
	}
	// Simply saves above
	storeLicenseDetails = (data) => {
		let tempState = this.state
		data.forEach((lic)=>{
			if(tempState.violators[lic.license]===undefined){
				tempState.violators[lic.license] = {};
			}
			tempState.violators[lic.license] = {...tempState.violators[lic.license],...lic}
		})
		this.setState(tempState);
	}

	// This will look for other licenses that may have the same organization name, so we can give the option to combine.
	getNaughty = () => {
		let naughtyCall = ['https://data.lcb.wa.gov/resource/dgm4-3cm6.json?'];
		naughtyCall.push('$select=license_number,count(penalty_type)');
		naughtyCall.push('&$group=(license_number)&$order=count(penalty_type)desc');
		// naughtyCall.push('&$limit=10');
		fetch(naughtyCall.join('')).then(response => response.json()).then(this.calculateNaughtiness)
	}
	// Determins the naughtiness of each license number, stored in a violators object, with the specific WAC codes in a seperate one
	calculateNaughtiness = (data) => {
		let tempState = this.state

		// This will load up the violators deatils for the listing
		data.forEach((violator)=>{
			let lic = violator.license_number
			tempState.violators[lic] = {};
			tempState.violators[lic].license_number = lic
			tempState.violators[lic].violations = violator.count_penalty_type
			tempState.violatorsOrdered.push(lic)
		});
		this.setState(tempState);
		this.getLicenseDetails(Object.keys(tempState.violators))

	}

	// Simply loops to call the list and renders it out, shared the SearchResults component.
	displayList = () => {
		let results = [];
		let safety = 0; // This will stop it quickly if the list is not loaded enough to provide results, if it ever finds 5 blank licenses it will stop
		let index = 0;
		while(results.length<this.state.limitor && safety<5){
			let lic = this.state.violatorsOrdered[index];
			if( this.state.violators[lic] !== undefined && this.state.violators[lic].organization !== undefined){
				// So it's a good one, push it and reset the safty check
				results.push(<SearchResult data={this.state.violators[lic]} type="naughtyList" key={'naughtyList'+lic}/>);
				safety=0;
			}else{
				safety++;
			}
			index++;
		}
		return results;
	}

	// This will change the limitor is state to the length of the ordered list (so they will al get checked if the saftey doesn't trip)
	loadAllButton = () => {
		if(this.state.violatorsOrdered.length<100){
			return null;
		}else if(this.state.limitor == this.state.violatorsOrdered.length){
			return null;
		}else{
			console.log(this.state.violatorsOrdered.length,this.state.limitor,this.state.limitor == this.state.violatorsOrdered.length)
			return (<button onClick={this.loadAll} id={'more-naughty'}>Load the Complete List</button>)
		}
	}
	loadAll = () => {
		let tempState = this.state;
		tempState.limitor = this.state.violatorsOrdered.length;
		this.setState(tempState);
	}

	render(){ 
		return (
			<Container>
				<Row>
					<Col>This is the naughty page, its kinda mess at the moment</Col>
				</Row>
				<ListGroup className={'row'} flush>
					{this.displayList()}
				</ListGroup>
				{this.loadAllButton()}
			</Container>
		)
	}
}
				// <pre>{JSON.stringify(this.state,null,2)}</pre>

export default NaughtyList;
