import React, { Component } from 'react'
// import { Link } from 'react-router-dom'
// import { push } from 'connected-react-router'
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux'

class Search extends Component {
	constructor(props) {
		super(props);

		// Simple state for just the search term
		this.state = {
			input: ''
		}
	}

	updateSearch = (e) => {
		let term = e.target.querySelector('#search-term').value
		this.state.input = term;
		e.preventDefault()
		this.getSearchResults();
	}

	getSearchResults = () => {
		console.log('You want to search for '+this.state.input)
		fetch('https://data.lcb.wa.gov/resource/bhbp-x4eb.json?$where=organization%20like%20%27%25'+this.state.input+'%25%27').then(response => response.json()).then((test)=>{console.log(test)})
	}

	render(){ return (
		<div>
			<i> Enter a name, license number, or UBI number</i><br/>
			<form onSubmit={this.updateSearch} id={'main-search-form'}>
			  	<input id={'search-term'} name={'search-term'} type="search" />
			  	<button>Search</button>
			</form>
			<div>
				I guess results will go here...
			</div>
		</div>

	) }

}

export default Search;
