import React, { Component } from 'react'
// import { Link } from 'react-router-dom'
// import { push } from 'connected-react-router'
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux'
import ReactTable from "react-table";

// Components
import SearchResult from '../search-result'

class Home extends Component {
	constructor(props) {
		super(props);

		// Simple state for just the search term
		console.log('this.state',this.state)
		this.state = {
			input: '',
			results: [],
		}
	}

	updateSearch = (e) => {
		let term = e.target.querySelector('#search-term').value
		this.state.input = term.toUpperCase();
		e.preventDefault()
		this.getSearchResults();
	}

	// This will make a call against the licenses enpoint with the search term. It's kinda messy but it searches against name, ubi, 
	// and lic# since you can easily find one of those things on the packaging (or so I've been told).
	getSearchResults = () => {
		let searchCall = ['https://data.lcb.wa.gov/resource/bhbp-x4eb.json?'];
		searchCall.push('$where=organization%20like%20%27%25'+this.state.input+'%25%27');
		searchCall.push('or license%20like%20%27'+this.state.input+'%25%27');
		searchCall.push('or ubi%20like%20%27'+this.state.input+'%25%27');
		searchCall.push('&$order=%20organization');
		fetch(searchCall.join('')).then(response => response.json()).then(this.storeSearchResults)
	}
	// This stores the latest search results in the state for display later
	storeSearchResults = (resultArray) => {
		let tempState = this.state
		tempState.results = resultArray
		this.setState(tempState)
	}

	// Simply loops to call the SearchResult component
	displaySearchResults = () => {
		let results = [];
		this.state.results.forEach((license) => {
			results.push(<SearchResult data={license} key={'searchResultsLic'+license.license}/>)
		})
		return results;
	}

	// This gives a count at the bottom of the page.
	displaySearchResultsSummary = () => {
		if(this.state.input=='' && this.state.results.length==0){
			return null
		}else if(this.state.results.length==0){
			return (<div className={'search-summary no-results'}>No licenses were found matching your search.</div>)
		// at 1000 the API caps, I could make it paginate and such but really the goal is to find a single entry, you need to refine your search if your finding 1000+
		}else if(this.state.results.length < 1000){ 
			return (<div className={'search-summary result-count'}>Found {this.state.results.length} licenses matching your search.</div>)
		}else{ // at 1000 resutls we are gonna guess there might be more
			return (<div className={'search-summary result-count'}>Found at least 1,000 licenses matching your search, perhaps you should refine it a bit.</div>)
		}
	}

	render(){ 
		return (
			<div>
				<form onSubmit={this.updateSearch} id={'main-search-form'}>
					<i> Enter a name, license number, or UBI number</i><br/>
				  	<input id={'search-term'} name={'search-term'} type="search" />
				  	<button>Search</button>
				</form>
				<ul>
					{this.displaySearchResults()}
				</ul>
				{this.displaySearchResultsSummary()}
			</div>
		)
	}
}

export default Home;
