import React, { Component } from 'react'
import ReactMarkdown from 'react-markdown'

// Components
import { Container, Row, Col } from 'reactstrap';

// const ReactDOM = require('react-dom')
const readme = require('../../linked-readme.md')
console.log(readme)

const input = '# This is a header\n\nAnd this is a paragraph'


class About extends Component {
	constructor(props) {
		super(props);
		this.state = {
			readme: ''
		}
		this.loadReadme();
	}

	// Simply load the markdown doc to be parsed
	loadReadme(){
		fetch('linked-readme.txt').then((r)=>{
			console.log(r.text);
		});
		fetch('../linked-readme.txt').then((r)=>{
			console.log(r.text);
		});
		fetch('../../linked-readme.txt').then((r)=>{
			console.log(r.text);
		});
		fetch('../../../linked-readme.txt').then((r)=>{
			console.log(r.text);
		});
	}

	// Simple output, not really looking for a bnuch of hubub
	render(){ 
		return (
			<Container>
				<ReactMarkdown source={input} />
				<ReactMarkdown source={input} />
				<ReactMarkdown source={this.state.readme} />
				<Row><Col><p>This is going to be a simple about page, why it was made and whatnot. Think of it as the future employer confirmation that I dont just sit around smoking pot all day (spoiler, I kinda do)</p></Col></Row>
			</Container>
		)
	}
}

export default About

