import React, { Component } from 'react'
import { Row, Col } from 'reactstrap';

class SearchResultsMessage extends Component {
	render(){ 
		if(this.props.searchStatus == 'waiting'){
			return null
		}else{
			let message = 'Searching the Washington State Liquor and Cannabis Control Board API...'
			if(this.props.searchStatus=='complete'){
				if(this.state.results.length < 1000){
					message = 'Found '+this.props.resultCount+'  licenses matching your search.'
				}else{// at 1000 resutls we are gonna guess there might be more
					message = 'Found at least 1,000 licenses matching your search, perhaps you should refine it a bit.';
				}
			}
			return (<Row><Col>{message}</Col><Col><pre>{JSON.stringify(this.props,null,2)}</pre></Col></Row>)
		}
	}
}

export default SearchResultsMessage;

