import React, { Component } from 'react'
// import { Link } from 'react-router-dom'
// import { push } from 'connected-react-router'
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux'
// import ReactTable from "react-table";

// Components
import SearchResult from '../search-result'

class Detail extends Component {
	constructor(props) {
		super(props);
		this.state = {
			'license': props.match.params.license,
			'visits': [],
			'violations': [],
			'combinesOrdered': []
		}
		this.getViolations();
	}

	// This will query the API and get a list of violations, then call the store function to store it appropriatly
	getViolations = () => {
		let violationsCall = ['https://data.lcb.wa.gov/resource/dgm4-3cm6.json?'];
		violationsCall.push('$where=organization = "'+this.state.license+'" ')
		violationsCall.push('&$order=visit_date desc')
		fetch(violationsCall.join('')).then(response => response.json()).then(this.storeAPIResults)
	}

	// This will store the API results depending on the call
	storeAPIResults = (things,a,b,c) => {
		console.log('storeAPIResults',things,a,b,c)
	}

	render(){ 
		console.log(this)
		return (
			<div>
				<h2>This is going to be the details page for lic#{this.state.license}</h2>
			</div>
		)
	}
}

export default Detail;
